INSERT INTO posts (id, is_active, moderation_status, time, title, view_count, moderator_id, user_id, text) VALUES(20, 1, 'ACCEPTED', '2020-09-21 00:05:52.008525', "Не новости", 1, 103, 101, "Издание De Telegraaf со ссылкой на свои источники сообщает, что гонка в Зандфорте запланирована на 5 сентября 2021 года. Подобный вариант кажется вполне правдоподобным, учитывая, что ранее упоминались строенные этапы в Спа, Зандфорте и Монце." );
INSERT INTO posts (id, is_active, moderation_status, time, title, view_count, moderator_id, user_id, text) VALUES(21, 1, 'DECLINED', '2020-10-21 00:05:52.008525', "Всякое бывает", 1000, 103, 102, "Выезд автомобиля безопасности в такой момент – худший из возможных сценариев. Мы надеялись на другое развитие событий, нам было сложно принять такое решение. В тот момент Серхио ехал на шинах Hard, мы специально настроили машину на длинную серию кругов и очень волновались по поводу рестарта. Нам казалось, что могут возникнуть проблемы." );
INSERT INTO posts (id, is_active, moderation_status, time, title, view_count, moderator_id, user_id, text) VALUES(22, 1, 'ACCEPTED', '2020-11-21 00:05:52.008525', "Булочки", 100, 103, 102, "Съешь же ещё этих мягких французских булок, да выпей чаю" );