INSERT INTO posts (id, is_active, moderation_status, time, title, view_count, moderator_id, user_id, text) VALUES(5, 1, 'ACCEPTED', '2020-05-21 00:05:52.008525', "POST FIVE",888, 103, 106, "Ти́хий океа́н — самый большой по площади и глубине океан на Земле. Расположен между материками Евразией и Австралией на западе, Северной и Южной Америкой на востоке, Антарктидой на юге. Тихий океан простирается приблизительно на 15,8 тыс. км с севера на юг и на 19,5 тыс. км с востока на запад." );
